package com.cocs.beans;import java.io.File;import java.io.IOException;import java.io.InputStream;import org.springframework.web.multipart.MultipartFile;import com.cocs.common.Env;public class MultipartFileResource {		private MultipartFile file;	public MultipartFileResource(MultipartFile file) {		this.file = file;	}	public String getFileName() {		return file.getOriginalFilename();	}	public long getFileSize() {		return file.getSize();	}	public InputStream getInputStream() {		try {			return file.getInputStream();		} catch (IOException e) {			throw new RuntimeException(e);		}	}	public String getMimeType() {		return file.getContentType();	}		public File getFile() {		File tempFile = new File(Env.getRepositoryUploadDirPath(), Long.toString(System.currentTimeMillis()));		try {			file.transferTo(tempFile);		} catch (IllegalStateException | IOException e) {			throw new RuntimeException(e);		}        return tempFile;	}}