package com.cocs.handler;import java.util.LinkedHashMap;import com.cocs.common.DefaultConstants;public class ResponseSuccessImpl extends LinkedHashMap<String, Object> implements ResponseSuccess<String, Object>{	private static final long serialVersionUID = -663191882797839098L;	private ResponseSuccessImpl() {		super();		put(DefaultConstants.SUCCESS,false);	}	public ResponseSuccessImpl(String vendor) {		this();		put("vendor",vendor);	}		public void setSuccess(boolean isSuccess){		put(DefaultConstants.SUCCESS,isSuccess);	}	@Override	public boolean isSuccess(){		return Boolean.parseBoolean(String.valueOf(get(DefaultConstants.SUCCESS)));	}	public void setId(String id){		put(DefaultConstants.ID, id);	}	@Override	public String getId(){		return (String) get(DefaultConstants.ID);	}}