package com.cocs.security;import javax.crypto.Cipher;import javax.crypto.KeyGenerator;import javax.crypto.SecretKey;import org.apache.commons.codec.binary.Base64;public class DesCrypt {	private static Cipher cipher;	private static SecretKey key;		static {		try {			cipher = Cipher.getInstance("DES/ECB/PKCS5Padding");			KeyGenerator keygenerator = KeyGenerator.getInstance("DES");			key = keygenerator.generateKey();		} catch (Exception e) {		}	}    public static String doEncode(String target) throws Exception {		cipher.init(Cipher.ENCRYPT_MODE, key);		byte[] targetStringByte = target.getBytes("UTF8");		byte[] encryptedByte = cipher.doFinal(targetStringByte);		String returnString = new String(Base64.encodeBase64(encryptedByte, false, true));		return returnString.trim();    }    public static String doDecode(String target) throws Exception {        cipher.init(Cipher.DECRYPT_MODE, key);        byte[] targetStringByte = Base64.decodeBase64(target.getBytes());        byte[] returnStringByte = cipher.doFinal(targetStringByte);        String returnString = new String(returnStringByte, "UTF8");        return returnString;    }}