package com.cocs.service;import java.io.Serializable;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Component;import twitter4j.Twitter;import com.dropbox.core.DbxClient;import com.google.api.services.drive.Drive;import facebook4j.Facebook;@Scope(value = "session")@Component("sessionContext")public class SessionContext implements Serializable{	private static final long serialVersionUID = 6887845918455422939L;		private DbxClient dropboxClient;	private Drive googleClient;	private Facebook facebookClient;	private Twitter twitterClient;		public DbxClient getDropboxClient() {		return dropboxClient;	}	public void setDropboxClient(DbxClient dropboxClient) {		this.dropboxClient = dropboxClient;	}		public Drive getGoogleClient() {		return googleClient;	}	public void setGoogleClient(Drive googleClient) {		this.googleClient = googleClient;	}	public Facebook getFacebookClient() {		return facebookClient;	}	public void setFacebookClient(Facebook facebookClient) {		this.facebookClient = facebookClient;	}		public Twitter getTwitterClient() {		return twitterClient;	}	public void setTwitterClient(Twitter twitterClient) {		this.twitterClient = twitterClient;	}		public void invalidate() {		this.dropboxClient = null;		this.googleClient = null;	}}